import React, { Component } from 'react';
import Login from './containerComponents/Login.js';
import BlockchainData from './containerComponents/BlockchainData.js';
import store from './redux/store';

import './css/App.css';

class App extends Component {

    constructor(props) {
        super(props);
        this.state = {
            etherAddress: '',
            login: false   
        };

        store.subscribe(() => {
            const data = store.getState().data;
            this.setState({ 
                etherAddr: data.etherAddress,
                login: data.login            
            });
        })
    }

    render() {
        return (
            <div>
               { this.state.login ? <BlockchainData etherAddress={this.state.etherAddr} /> : <Login/>}
            </div>
        );
    }
}

export default App;
