import { SPECIFY_ADDRESS, SET_WEB3, SET_MULTISIG_CONTRACT, SET_MULTISIG_ERC_CONTRACT, SET_TOKEN_IMPERIAL, SET_TOKEN_DEMOCRATIC, UPDATE_ETHER_BALANCE } from '../actionTypes';

const initialState = {
	web3: '',
	multisig: '',
	multisigERC20: '',
	etherAddress: '',
	etherBalance: '',
	contractBalance: '',
	tokenImperial: '',
	tokenDemocratic: '',
	tokenImperialBalance: '',
	tokenDemocraticContractBalance: '',
	tokenImperialContractBalance: '',
	login: false
};

export default function handlingActions(state = initialState, action) {
	switch (action.type) {
    case SPECIFY_ADDRESS:
		return Object.assign({}, state, {
			etherAddress: action.payload,
			login: true
		});
	case SET_WEB3:
		return Object.assign({}, state, {
			web3: action.payload
		});
	case SET_MULTISIG_CONTRACT:
		return Object.assign({}, state, {
			multisig: action.payload
		});
	case SET_MULTISIG_ERC_CONTRACT:
		return Object.assign({}, state, {
			multisigERC20: action.payload
		});	
	case SET_TOKEN_IMPERIAL:
		return Object.assign({}, state, {
			tokenImperial: action.payload
		});
	case SET_TOKEN_DEMOCRATIC:
		return Object.assign({}, state, {
			tokenDemocratic: action.payload
		});
	case UPDATE_ETHER_BALANCE:
		state.web3.eth.getBalance(state.etherAddress, (error, balance) => {
				if (error) {
				console.log(error);
			} else {
				console.log('New ether balance from redux: ', balance);
				return Object.assign({}, state, {
					etherBalance: balance
				});
			}
		});
		break;								
    default:
      return state;
	}
}
