import { SPECIFY_ADDRESS, SET_WEB3 } from '../actionTypes';

const initialState = {
	web3: '',
	etherAddress: '',
	login: false
};

export default function handlingActions(state = initialState, action) {
	switch (action.type) {
    case SPECIFY_ADDRESS:
		return Object.assign({}, state, {
			etherAddress: action.payload,
			login: true
		});
	case SET_WEB3:
		return Object.assign({}, state, {
			web3: action.payload
		});
    default:
      return state;
	}
}
