import React, { Component } from 'react';
import Login from './containerComponents/Login.js';
import BlockchainData from './containerComponents/BlockchainData.js';
import { connect } from 'react-redux';
import './css/App.css';

class App extends Component {

    constructor(props) {
        super(props);
        this.state = {
            etherAddress: '',
            login: false   
        };
    }

    setAddress = (address) => {
        this.setState({ 
            etherAddress: address,
            login: true
        });
    }

    render() {
        return (
            <div>
               { this.state.login ? <BlockchainData etherAddress={this.state.etherAddr} /> : <Login setAddress={this.setAddress}/>}
            </div>
        );
    }
}

const mapStateToProps = state => ({
    etherAddr: state.data.etherAddress
});

export default connect(mapStateToProps, {})(App);
